import React from 'react';

const Footer = () => {
  return (
    <footer style={footerStyle}>
      <div style={leftContentStyle}>
        <img
          src="seculus-logo-cortado.png" // Substitua pelo caminho da sua logo
          alt="Logo da sua empresa"
          style={logoStyle}
        />
        <p style={textStyle}>A Seculus Lawtech é uma empresa especializada em otimizar o pagamento\
         das guias de custas judiciais dos processos. Nossa missão é simplificar e agilizar o processo\
          de pagamento de custas judiciais, oferecendo soluções eficientes para escritórios de advocacia,\
           departamentos jurídicos e profissionais do meio jurídico. Com a nossa tecnologia avançada, \
           garantimos que você tenha total controle sobre os pagamentos, evitando atrasos e reduzindo custos\ operacionais. Confie na Seculus Lawtech para uma gestão inteligente e eficaz das guias de custas judiciais, \permitindo que você concentre-se no que realmente importa: a excelência em sua prática jurídica."</p>
      </div>
      <div style={rightContentStyle}>
        <a href="https://www.linkedin.com/company/seculus-lawtech/" target="_blank" rel="noopener noreferrer">
          LinkedIn
        </a>
        <a href="https://api.whatsapp.com/send?phone=5531971102606&text=Oi%2C+tudo+bem%3F+Es\
        tou+com+um+processo%21+Como+pode+me+ajudar%3F&_fb_noscript=1" target="_blank" rel="noopener noreferrer">
          WhatsApp
        </a>
      </div>
    </footer>
  );
};

// Estilos para o footer e os conteúdos esquerdo e direito
const footerStyle = {
  backgroundColor: 'rgb(89, 97, 92)',
  padding: '10px',
  display: 'flex',
  justifyContent: 'space-between',
};

const leftContentStyle = {
  display: 'flex',
  flexDirection: 'column', // Renderiza os elementos em coluna
  alignItems: 'flex-start', // Alinha os elementos à esquerda
};

const logoStyle = {
  width: '40px', // Defina o tamanho adequado para a sua logo
  marginRight: '10px', // Espaçamento entre a logo e o texto
  marginLeft: '40px', // Distância da logo em relação à borda esquerda
  marginTop: '20px',
};

const textStyle = {
  marginLeft: '40px', // Mesma margem da logo à esquerda
};

const rightContentStyle = {
  flex: 1,
  display: 'flex',
  justifyContent: 'flex-end',
};

export default Footer;
